import React, { useState, useEffect } from 'react';
import Navigation from './components/Navigation';
import SentenceCard from './components/SentenceCard';
import { SentenceData, Sentence } from './types';
import './styles/App.css';

const App: React.FC = () => {
  const [availableFiles, setAvailableFiles] = useState<string[]>([]);
  const [pastMonths, setPastMonths] = useState<string[]>([]);
  const [selectedMonth, setSelectedMonth] = useState<string>('2025-08');
  const [selectedDate, setSelectedDate] = useState<string>('2025-08-07');
  const [sentenceData, setSentenceData] = useState<SentenceData | null>(null);
  const [sentences, setSentences] = useState<Sentence[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');
  const [isDarkMode, setIsDarkMode] = useState<boolean>(() => {
    const saved = localStorage.getItem('isDarkMode');
    return saved ? JSON.parse(saved) : false;
  });

  // Í≥ºÍ±∞ Ïõî Î™©Î°ù Î°úÎìú
  useEffect(() => {
    const loadPastMonths = async () => {
      try {
        // manifest.jsonÏóêÏÑú Í≥ºÍ±∞ ÌååÏùº Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
        const manifestResponse = await fetch('/data/past/manifest.json');
        if (manifestResponse.ok) {
          const manifest = await manifestResponse.json();
          const files = manifest.files || [];

          // ÌååÏùºÎ™ÖÏóêÏÑú Ïõî Ï†ïÎ≥¥ Ï∂îÏ∂ú (past202201.json -> 2022-01)
          const months = files.map((filename: string) => {
            const match = filename.match(/past(\d{4})(\d{2})\.json/);
            if (match) {
              return `${match[1]}-${match[2]}`;
            }
            return null;
          }).filter(Boolean);

          setPastMonths(months);
          console.log('Past months loaded:', months);
        }
      } catch (err) {
        console.error('Error loading past months:', err);
        // Fallback: ÏßÅÏ†ë ÏãúÎèÑÌï¥Î≥¥Í∏∞
        try {
          const testFile = await fetch('/data/past/past202201.json');
          if (testFile.ok) {
            setPastMonths(['2022-01']); // ÏµúÏÜåÌïú ÌïòÎÇòÎäî ÏûàÏùåÏùÑ ÌôïÏù∏
          }
        } catch (e) {
          console.error('No past files found');
        }
      }
    };
    loadPastMonths();
  }, []);

  // Ï¥àÍ∏∞ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    const loadInitialData = async () => {
      setLoading(true);
      setError('');

      try {
        console.log('Fetching data from /data/sentences202508.json');
        const response = await fetch('/data/sentences202508.json');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data: SentenceData = await response.json();
        console.log('Data loaded successfully:', data);
        console.log('Contents array:', data.contents);

        if (!data || !data.contents || !Array.isArray(data.contents) || data.contents.length === 0) {
          throw new Error('Invalid data structure');
        }

        // Ï≤´ Î≤àÏß∏ ÎÇ†ÏßúÏùò Î¨∏Ïû•Îì§ Í∞ÄÏ†∏Ïò§Í∏∞
        const firstDateData = data.contents[0];
        console.log('First date data:', firstDateData);
        console.log('Sentences:', firstDateData.sentences);

        setSentenceData(data);
        setSentences(firstDateData.sentences);

        // Ïã§Ï†ú JSON ÌååÏùºÏùò ÎÇ†ÏßúÎì§Î°ú ÏÑ§Ï†ï
        const actualDates = data.contents.map(content => content.date);
        console.log('Actual dates from JSON:', actualDates);

        setAvailableFiles(actualDates);
        if (actualDates.length > 0) {
          const firstDate = actualDates[0];
          const month = firstDate.substring(0, 7); // 2025-08
          setSelectedMonth(month);
          setSelectedDate(firstDate);
        }

      } catch (err) {
        console.error('Error loading data:', err);
        setError(err instanceof Error ? err.message : 'Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
        setSentences([]);
      } finally {
        setLoading(false);
      }
    };

    loadInitialData();
  }, []);

  // Ïõî Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleMonthChange = (month: string) => {
    console.log('Month changed to:', month);
    setSelectedMonth(month);

    // Ìï¥Îãπ ÏõîÏùò Ï≤´ Î≤àÏß∏ ÎÇ†Ïßú ÏÑ†ÌÉù
    const monthDates = availableFiles.filter(date => date.startsWith(month));
    if (monthDates.length > 0) {
      setSelectedDate(monthDates[0]);
      // Ïó¨Í∏∞ÏÑú ÏÉàÎ°úÏö¥ Îç∞Ïù¥ÌÑ∞Î•º Î°úÎìúÌï¥Ïïº Ìï® (ÌòÑÏû¨Îäî 2025-08Îßå ÏûàÏùå)
    }
  };

  // Í≥ºÍ±∞ Ïõî ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  const handlePastMonthChange = async (month: string) => {
    console.log('Past month selected:', month);
    setLoading(true);
    setError('');

    try {
      // Í≥ºÍ±∞ Ìè¥ÎçîÏóêÏÑú Îç∞Ïù¥ÌÑ∞ Î°úÎìú
      const monthCode = month.replace('-', '');
      const response = await fetch(`/data/past/sentences${monthCode}.json`);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data: SentenceData = await response.json();
      console.log('Past data loaded successfully:', data);

      if (!data || !data.contents || !Array.isArray(data.contents) || data.contents.length === 0) {
        throw new Error('Invalid data structure');
      }

      // Ï≤´ Î≤àÏß∏ ÎÇ†ÏßúÏùò Î¨∏Ïû•Îì§ Í∞ÄÏ†∏Ïò§Í∏∞
      const firstDateData = data.contents[0];
      setSentenceData(data);
      setSentences(firstDateData.sentences);

      // ÎÇ†Ïßú Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏
      const actualDates = data.contents.map(content => content.date);
      setAvailableFiles(actualDates);

      if (actualDates.length > 0) {
        const firstDate = actualDates[0];
        setSelectedMonth(month);
        setSelectedDate(firstDate);
      }
    } catch (err) {
      console.error('Error loading past data:', err);
      setError(err instanceof Error ? err.message : 'Í≥ºÍ±∞ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.');
      setSentences([]);
    } finally {
      setLoading(false);
    }
  };

  // ÎÇ†Ïßú Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleDateChange = (date: string) => {
    console.log('Date changed to:', date);
    setSelectedDate(date);

    // ÏÑ†ÌÉùÎêú ÎÇ†ÏßúÏóê Ìï¥ÎãπÌïòÎäî Î¨∏Ïû• Îç∞Ïù¥ÌÑ∞ Ï∞æÍ∏∞
    if (sentenceData && sentenceData.contents) {
      const selectedDateData = sentenceData.contents.find(content => content.date === date);
      if (selectedDateData) {
        console.log('Found data for date:', date, selectedDateData);
        setSentences(selectedDateData.sentences);
      } else {
        console.log('No data found for date:', date);
        setSentences([]);
      }
    }
  };


  // Îã§ÌÅ¨ Î™®Îìú ÌÜ†Í∏Ä
  const toggleDarkMode = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('isDarkMode', JSON.stringify(newMode));
  };

  if (loading) {
    return (
      <div className="app">
        <div className="loading">Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <div className="error">
          <h2>Ïò§Î•ò Î∞úÏÉù</h2>
          <p>{error}</p>
          <p>Î∏åÎùºÏö∞Ï†Ä ÏΩòÏÜî(F12)ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`app ${isDarkMode ? 'dark-mode' : ''}`}>
      <header className="app-header">
        <div className="header-title">
          <h1>ÏßÑÏ∞®Ïù¥Î¶∞ Ï§ëÍµ≠Ïñ¥</h1>
          <button 
            type="button"
            className="theme-toggle" 
            onClick={toggleDarkMode}
            title={isDarkMode ? "ÎùºÏù¥Ìä∏ Î™®ÎìúÎ°ú Î≥ÄÍ≤Ω" : "Îã§ÌÅ¨ Î™®ÎìúÎ°ú Î≥ÄÍ≤Ω"}
          >
            {isDarkMode ? '‚òÄÔ∏è' : 'üí°'}
          </button>
        </div>
        {/* <div className="date-info">{formatDateDisplay()}</div> */}
      </header>

      {availableFiles.length > 0 && (
        <Navigation
          availableDates={availableFiles}
          pastMonths={pastMonths}
          selectedMonth={selectedMonth}
          selectedDate={selectedDate}
          onMonthChange={handleMonthChange}
          onDateChange={handleDateChange}
          onPastMonthChange={handlePastMonthChange}
        />
      )}

      <main className="content">
        {sentences.length > 0 ? (
          <div className="sentences-container">
            {sentences.map((sentence) => (
              <SentenceCard key={sentence.id} sentence={sentence} />
            ))}
          </div>
        ) : (
          <div className="error">
            <h2>Îç∞Ïù¥ÌÑ∞ ÏóÜÏùå</h2>
            <p>ÌëúÏãúÌï† Î¨∏Ïû•Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default App;
