import React, { useState } from 'react';
import './styles/App.css';

type ViewMode = 'legacy' | 'new';
type DataCategory = 'currently' | 'integrated';
type IntegratedType = '01_초급반_제1-10과' | '02_중급반_제11-25과' | '03_고급반_제26-40과' | '04_실전회화_제41-50과' | '05_패턴_제1-30과';
type CurrentlyType = '202508';
type DisplayMode = 'sentence' | 'full' | 'words';

interface LessonData {
  id: number;
  lesson?: string;
  sentence?: string;
  pinyin?: string;
  korean?: string;
  english?: string;
  japanese?: string;
  japanese_romaji?: string;
  words?: any[];
}

function App() {
  const [viewMode, setViewMode] = useState<ViewMode | null>(null);
  const [dataCategory, setDataCategory] = useState<DataCategory | null>(null);
  const [selectedType, setSelectedType] = useState<IntegratedType | CurrentlyType | null>(null);
  const [selectedLesson, setSelectedLesson] = useState<LessonData[] | null>(null);
  const [displayMode, setDisplayMode] = useState<DisplayMode>('sentence');
  const [lessonData, setLessonData] = useState<any>(null);

  const goBack = () => {
    if (selectedLesson) {
      setSelectedLesson(null);
      setDisplayMode('sentence');
    } else if (selectedType) {
      setSelectedType(null);
    } else if (dataCategory) {
      setDataCategory(null);
    } else if (viewMode) {
      setViewMode(null);
    }
  };

  const loadLessonData = async (type: IntegratedType | CurrentlyType) => {
    try {
      const response = await fetch(`/data/integrated/${type}_complete.json`);
      const data = await response.json();
      setLessonData(data);

      // Extract unique lessons for lesson selection
      const lessons = data.reduce((acc: any[], item: any) => {
        const lessonNum = item.lesson || `Lesson ${item.id}`;
        if (!acc.find(l => l.lesson === lessonNum)) {
          acc.push({ lesson: lessonNum, id: item.id });
        }
        return acc;
      }, []);

      if (lessons.length === 1) {
        // If only one lesson, go directly to content
        setSelectedLesson(data);
      }
    } catch (error) {
      console.error('Failed to load lesson data:', error);
    }
  };

  const selectLesson = (lessonName: string) => {
    if (lessonData) {
      const lessonContent = lessonData.filter((item: any) => 
        item.lesson === lessonName || `Lesson ${item.id}` === lessonName
      );
      setSelectedLesson(lessonContent);
    }
  };

  // Initial view mode selection
  if (!viewMode) {
    return (
      <div className="app">
        <div className="view-mode-selection">
          <h1>중국어 학습 앱</h1>
          <button onClick={() => setViewMode('legacy')} className="view-mode-btn">
            기존 UI (Past/Present)
          </button>
          <button onClick={() => setViewMode('new')} className="view-mode-btn">
            새 UI (Currently/Integrated)
          </button>
        </div>
      </div>
    );
  }

  // Legacy UI placeholder (기존 UI 그대로 유지)
  if (viewMode === 'legacy') {
    return (
      <div className="app">
        <div className="header">
          <button onClick={goBack} className="back-btn">← 뒤로가기</button>
          <h2>기존 UI (Past/Present)</h2>
        </div>
        <div className="legacy-content">
          <p>기존 UI 컴포넌트를 여기에 표시합니다.</p>
        </div>
      </div>
    );
  }

  // New UI
  if (viewMode === 'new') {
    // Data category selection
    if (!dataCategory) {
      return (
        <div className="app">
          <div className="header">
            <button onClick={goBack} className="back-btn">← 뒤로가기</button>
            <h2>새 UI</h2>
          </div>
          <div className="data-category-selection">
            <button onClick={() => setDataCategory('currently')} className="category-btn">
              Currently
            </button>
            <button onClick={() => setDataCategory('integrated')} className="category-btn">
              Integrated
            </button>
          </div>
        </div>
      );
    }

    // Type selection
    if (!selectedType) {
      return (
        <div className="app">
          <div className="header">
            <button onClick={goBack} className="back-btn">← 뒤로가기</button>
            <h2>{dataCategory === 'currently' ? 'Currently' : 'Integrated'}</h2>
          </div>
          <div className="type-selection">
            {dataCategory === 'integrated' ? (
              <>
                <button onClick={() => { setSelectedType('초급반_제1-10과'); loadLessonData('01_초급반_제1-10과'); }} className="type-btn">초급반 (제1-10과)</button>
                <button onClick={() => { setSelectedType('중급반_제11-25과'); loadLessonData('02_중급반_제11-25과'); }} className="type-btn">중급반 (제11-25과)</button>
                <button onClick={() => { setSelectedType('고급반_제26-40과'); loadLessonData('03_고급반_제26-40과'); }} className="type-btn">고급반 (제26-40과)</button>
                <button onClick={() => { setSelectedType('실전회화_제41-50과'); loadLessonData('04_실전회화_제41-50과'); }} className="type-btn">실전회화 (제41-50과)</button>
                <button onClick={() => { setSelectedType('패턴_제1-90과'); loadLessonData('05_패턴_제1-30과'); }} className="type-btn">패턴회화 (제1-90과)</button>
              </>
            ) : (
              <button onClick={() => { setSelectedType('202508'); loadLessonData('202508' as any); }} className="type-btn">202508</button>
            )}
          </div>
        </div>
      );
    }

    // Lesson selection (if multiple lessons exist)
    if (selectedType && !selectedLesson && lessonData) {
      const lessons = lessonData.reduce((acc: any[], item: any) => {
        const lessonNum = item.lesson || `Lesson ${item.id}`;
        if (!acc.find(l => l.lesson === lessonNum)) {
          acc.push({ lesson: lessonNum, id: item.id });
        }
        return acc;
      }, []);

      if (lessons.length > 1) {
        return (
          <div className="app">
            <div className="header">
              <button onClick={goBack} className="back-btn">← 뒤로가기</button>
              <h2>{selectedType} - 레슨 선택</h2>
            </div>
            <div className="lesson-selection">
              {lessons.map((lesson: any, index: number) => (
                <button 
                  key={index} 
                  onClick={() => selectLesson(lesson.lesson)} 
                  className="lesson-btn"
                >
                  {lesson.lesson}
                </button>
              ))}
            </div>
          </div>
        );
      }
    }

    // Content display
    if (selectedLesson) {
      return (
        <div className="app">
          <div className="header">
            <button onClick={goBack} className="back-btn">← 뒤로가기</button>
            <h2>{selectedType}</h2>
          </div>

          {/* Content area - 90% */}
          <div className="content-area">
            {selectedLesson.map((item: LessonData, index: number) => (
              <div key={index} className="content-item">
                {displayMode === 'sentence' && (
                  <div className="sentence-only">
                    <p className="chinese">{item.sentence}</p>
                  </div>
                )}

                {displayMode === 'full' && (
                  <div className="full-details">
                    <p className="chinese">{item.sentence}</p>
                    <p className="pinyin">{item.pinyin}</p>
                    <p className="korean">{item.korean}</p>
                    <p className="english">{item.english}</p>
                    <p className="japanese">{item.japanese}</p>
                    <p className="japanese-romaji">{item.japanese_romaji}</p>
                  </div>
                )}

                {displayMode === 'words' && (
                  <div className="words-only">
                    {item.words && item.words.map((word: any, wIndex: number) => (
                      <div key={wIndex} className="word-item">
                        <span className="word-chinese">{word.chinese}</span>
                        <span className="word-pinyin">{word.pinyin}</span>
                        <span className="word-korean">{word.korean}</span>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>

          {/* Control buttons - 10% */}
          <div className="control-buttons">
            <button 
              onClick={() => setDisplayMode('sentence')} 
              className={`control-btn ${displayMode === 'sentence' ? 'active' : ''}`}
            >
              문장만
            </button>
            <button 
              onClick={() => setDisplayMode('full')} 
              className={`control-btn ${displayMode === 'full' ? 'active' : ''}`}
            >
              전체 정보
            </button>
            <button 
              onClick={() => setDisplayMode('words')} 
              className={`control-btn ${displayMode === 'words' ? 'active' : ''}`}
            >
              단어만
            </button>
            <button className="control-btn">버튼4</button>
            <button className="control-btn">버튼5</button>
          </div>
        </div>
      );
    }
  }

  return <div>Loading...</div>;
}

export default App;
