import React, { useState } from 'react';
import { Sentence } from '../types';
import WordCard from './WordCard';

interface SentenceCardProps {
  sentence: Sentence;
}

const SentenceCard: React.FC<SentenceCardProps> = ({ sentence }) => {
  const [showDetails, setShowDetails] = useState(false);
  const [showWords, setShowWords] = useState(false);

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  const toggleWords = () => {
    setShowWords(!showWords);
  };

  const playAudio = async (event: React.MouseEvent) => {
    event.stopPropagation(); // Î∂ÄÎ™® ÌÅ¥Î¶≠ Ïù¥Î≤§Ìä∏ Î∞©ÏßÄ

    const text = sentence.sentence;

    try {
      // 1. ResponsiveVoice API ÏãúÎèÑ (ÎÑ§Ïù¥Î≤Ñ Ïï± Î∏åÎùºÏö∞Ï†Ä ÏßÄÏõê)
      if ((window as any).responsiveVoice) {
        (window as any).responsiveVoice.speak(text, "Chinese Female", {
          rate: 0.9,
          pitch: 1,
          volume: 1
        });
        return;
      }

      // 2. Web Speech API ÏãúÎèÑ
      if ('speechSynthesis' in window) {
        const utterance = new SpeechSynthesisUtterance(text);
        utterance.lang = 'zh-CN';
        utterance.rate = 0.9;
        window.speechSynthesis.speak(utterance);
        return;
      }

      // 3. Î™®Îì† Î∞©Î≤ïÏù¥ Ïã§Ìå®ÌïòÎ©¥ Google Translate ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      const googleTranslateUrl = `https://translate.google.com/?sl=zh-CN&tl=ko&text=${encodeURIComponent(text)}&op=translate`;

      // ÏÉà Ï∞ΩÏúºÎ°ú Ïó¥Í∏∞ (ÏÇ¨Ïö©ÏûêÍ∞Ä ÏßÅÏ†ë Ïä§ÌîºÏª§ Î≤ÑÌäº ÌÅ¥Î¶≠ Í∞ÄÎä•)
      const userConfirmed = (window as any).confirm('ChromeÏù¥ÎÇò Safari, EDGEÏóêÏÑú Ï†ëÏÜçÌïòÏÑ∏Ïöî. Îã§Î•∏ Î∏åÎùºÏö∞Ï†ÄÏóêÏÑúÎäî ÏùåÏÑ± Ïû¨ÏÉùÏù¥ ÏßÄÏõêÎêòÏßÄ ÏïäÏäµÎãàÎã§. Google Î≤àÏó≠ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÏó¨ ÏùåÏÑ±ÏùÑ Ïû¨ÏÉùÌïòÏãúÍ≤†ÏäµÎãàÍπå?');
      if (userConfirmed) {
        (window as any).open(googleTranslateUrl, '_blank');
      }

    } catch (error) {
      console.error('Audio playback error:', error);
      // alert ÎåÄÏã† console.error ÏÇ¨Ïö© (ÎòêÎäî ÏÇ¨Ïö©Ïûê Ï†ïÏùò ÏïåÎ¶º Ïª¥Ìè¨ÎÑåÌä∏)
      console.error('ÏùåÏÑ± Ïû¨ÏÉùÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Î∏åÎùºÏö∞Ï†ÄÎ•º ChromeÏù¥ÎÇò SafariÎ°ú Î≥ÄÍ≤ΩÌï¥Î≥¥ÏÑ∏Ïöî.');
    }
  };

  return (
    <div className="sentence-card">
      <span className="sentence-number"> {sentence.id}</span>
      <div className="chinese-sentence-container">
        <div className="chinese-sentence" onClick={toggleDetails} style={{cursor: 'pointer'}}>
          {sentence.sentence}
        </div>
        <button 
          type="button"
          className="audio-button" 
          onClick={playAudio}
          title="ÏùåÏÑ± Ïû¨ÏÉù"
        >
          üîä
        </button>
      </div>

      <div className={`sentence-details ${showDetails ? 'expanded' : ''}`}>
        <div className="pinyin">{sentence.pinyin}</div>

        <div className="meanings">
          <div className="meaning-item">
            <div className="meaning-label">üá∞üá∑ ÌïúÍµ≠Ïñ¥</div>
            <div className="meaning-text">{sentence['meaning-korean']}</div>
          </div>
          <div className="meaning-item">
            <div className="meaning-label">üá∫üá∏ English</div>
            <div className="meaning-text">{sentence['meaning-english']}</div>
          </div>
        </div>

        <div className="words-section">
          <div className="words-toggle" onClick={toggleWords}>
            <div className="words-title">üìö Îã®Ïñ¥ ÌïôÏäµ ({sentence.words.length}Í∞ú)</div>
            <span className={`toggle-arrow ${showWords ? 'expanded' : ''}`}>
              ‚ñº
            </span>
          </div>
          <div className={`words-content ${showWords ? 'expanded' : ''}`}>
            <div className="words-grid">
              {sentence.words.map((word, index) => (
                <WordCard key={index} word={word} />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SentenceCard;
