import React from 'react';
import { SentenceData, isCategoryContent, isDateBasedContent } from '../types';

interface NavigationProps {
  availableDates: string[];
  pastMonths: string[];
  selectedMonth: string;
  selectedDate: string;
  selectedCategory: string;
  selectedSubcategory: string;
  sentenceData: SentenceData | null;
  isDateBased: boolean;
  onMonthChange: (month: string) => void;
  onDateChange: (date: string) => void;
  onCategoryChange: (category: string) => void;
  onSubcategoryChange: (subcategory: string) => void;
  onPastMonthChange: (month: string) => void;
}

const Navigation: React.FC<NavigationProps> = ({
  availableDates,
  pastMonths,
  selectedMonth,
  selectedDate,
  selectedCategory,
  selectedSubcategory,
  sentenceData,
  isDateBased,
  onMonthChange,
  onDateChange,
  onCategoryChange,
  onSubcategoryChange,
  onPastMonthChange,
}) => {
  const months = [...new Set(availableDates.map(date => date.substring(0, 7)))];
  const currentMonthDates = availableDates.filter(date => date.startsWith(selectedMonth));

  // Get categories and subcategories for category-based structure
  const categories = sentenceData?.contents
    .filter(isCategoryContent)
    .map(content => content.category) || [];

  const subcategories = sentenceData?.contents
    .filter(isCategoryContent)
    .find(content => content.category === selectedCategory)
    ?.subcategories.map(subcat => subcat.subcategory) || [];

  const formatMonth = (month: string) => {
    const [year, monthNum] = month.split('-');
    return `${year}년 ${monthNum}월`;
  };

  const formatDate = (date: string) => {
    const day = date.split('-')[2];
    return `${day}일`;
  };

  return (
    <nav className="navigation">
      <div className="selectors-container">
        <div className="month-selector">
          <label htmlFor="month-select">현재 학습</label>
          <select
            id="month-select"
            value={selectedMonth}
            onChange={(e) => onMonthChange(e.target.value)}
          >
            {months.map(month => (
              <option key={month} value={month}>
                {formatMonth(month)}
              </option>
            ))}
          </select>
        </div>

        <div className="past-month-selector">
          <label htmlFor="past-month-select">과거 자료</label>
          <select
            id="past-month-select"
            value=""
            onChange={(e) => e.target.value && onPastMonthChange(e.target.value)}
          >
            <option value="">과거 월 선택</option>
            {pastMonths.map(month => (
              <option key={month} value={month}>
                {formatMonth(month)}
              </option>
            ))}
          </select>
        </div>
      </div>

      {isDateBased ? (
        // Date-based navigation (old format)
        <div className="date-tabs">
          {currentMonthDates.map(date => (
            <button
              key={date}
              className={`date-tab ${selectedDate === date ? 'active' : ''}`}
              onClick={() => onDateChange(date)}
            >
              {formatDate(date)}
            </button>
          ))}
        </div>
      ) : (
        // Category-based navigation (new format)
        <>
          <div className="category-tabs">
            {categories.map(category => (
              <button
                key={category}
                className={`category-tab ${selectedCategory === category ? 'active' : ''}`}
                onClick={() => onCategoryChange(category)}
              >
                {category}
              </button>
            ))}
          </div>

          {subcategories.length > 0 && (
            <div className="subcategory-tabs">
              {subcategories.map(subcategory => (
                <button
                  key={subcategory}
                  className={`subcategory-tab ${selectedSubcategory === subcategory ? 'active' : ''}`}
                  onClick={() => onSubcategoryChange(subcategory)}
                >
                  {subcategory}
                </button>
              ))}
            </div>
          )}
        </>
      )}
    </nav>
  );
};

export default Navigation;
