import React, { useState, useEffect } from 'react';
import Navigation from './components/Navigation';
import SentenceCard from './components/SentenceCard';
import { SentenceData, Sentence } from './types';
import './styles/App.css';

const App: React.FC = () => {
  const [availableFiles, setAvailableFiles] = useState<string[]>([]);
  const [selectedMonth, setSelectedMonth] = useState<string>('202508');
  const [selectedDate, setSelectedDate] = useState<string>('20250807');
  const [sentenceData, setSentenceData] = useState<SentenceData | null>(null);
  const [sentences, setSentences] = useState<Sentence[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  // 초기 데이터 로드 (간단한 버전)
  useEffect(() => {
    const loadInitialData = async () => {
      setLoading(true);
      try {
        // 단순히 202508 파일만 로드
        const response = await fetch('/data/sentences202508.json');
        console.log('Response status:', response.status);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data: SentenceData = await response.json();
        console.log('Loaded data:', data);

        setSentenceData(data);
        setSentences(data.contents.sentences);

        // 네비게이션을 위한 기본 설정
        setAvailableFiles(['20250807']);
        setSelectedMonth('202508');
        setSelectedDate('20250807');

      } catch (err) {
        console.error('Error loading data:', err);
        setError(err instanceof Error ? err.message : '데이터를 불러올 수 없습니다.');
      } finally {
        setLoading(false);
      }
    };

    loadInitialData();
  }, []);

  const handleMonthChange = (month: string) => {
    setSelectedMonth(month);
    // 현재는 한 달 데이터만 있으므로 기능 제한
  };

  const handleDateChange = (date: string) => {
    setSelectedDate(date);
    // 현재는 하루 데이터만 있으므로 기능 제한
  };

  if (loading) {
    return (
      <div className="app">
        <div className="loading">데이터를 불러오는 중...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <div className="error">
          <h2>오류 발생</h2>
          <p>{error}</p>
          <p>브라우저 콘솔을 확인해주세요.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="app-header">
        <h1>중국어 문장 학습</h1>
        {sentenceData && (
          <div className="date-info">2025년 08월 07일</div>
        )}
      </header>

      {availableFiles.length > 0 && (
        <Navigation
          availableDates={availableFiles}
          selectedMonth={selectedMonth}
          selectedDate={selectedDate}
          onMonthChange={handleMonthChange}
          onDateChange={handleDateChange}
        />
      )}

      <main className="content">
        {sentences.length > 0 ? (
          <div className="sentences-container">
            {sentences.map((sentence) => (
              <SentenceCard key={sentence.id} sentence={sentence} />
            ))}
          </div>
        ) : (
          <div className="error">표시할 문장이 없습니다.</div>
        )}
      </main>
    </div>
  );
};

export default App;
