import React, { useState, useEffect } from 'react';
import Navigation from './components/Navigation';
import SentenceCard from './components/SentenceCard';
import { SentenceData, Sentence } from './types';
import './styles/App.css';

const App: React.FC = () => {
  const [availableFiles, setAvailableFiles] = useState<string[]>([]);
  const [selectedMonth, setSelectedMonth] = useState<string>('');
  const [selectedDate, setSelectedDate] = useState<string>('');
  const [sentenceData, setSentenceData] = useState<SentenceData | null>(null);
  const [sentences, setSentences] = useState<Sentence[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  // 사용 가능한 JSON 파일 목록 가져오기
  useEffect(() => {
    const fetchAvailableFiles = async () => {
      try {
        // 실제로는 서버 API에서 가져와야 하지만, 현재는 하드코딩
        // 월별로 파일이 추가되면 여기에 추가
        const files = [
          '202508', // sentences202508.json
          // '202509', // sentences202509.json (예시)
          // '202510', // sentences202510.json (예시)
        ];

        // 각 파일에서 사용 가능한 날짜 추출
        const dates: string[] = [];
        for (const month of files) {
          try {
            const response = await fetch(`/data/sentences${month}.json`);
            if (response.ok) {
              const data: SentenceData = await response.json();
              // 현재는 단일 날짜만 있지만, 추후 여러 날짜 지원 가능
              dates.push(month + data.contents.date.substring(8, 10));
            }
          } catch {
            console.error(`Failed to load ${month}`);
          }
        }

        if (dates.length > 0) {
          setAvailableFiles(dates);
          const defaultMonth = dates[0].substring(0, 6);
          setSelectedMonth(defaultMonth);
          setSelectedDate(dates[0]);
        }
      } catch (err) {
        setError('파일 목록을 불러올 수 없습니다.');
      }
    };

    fetchAvailableFiles();
  }, []);

  // 선택된 날짜의 데이터 로드
  useEffect(() => {
    if (!selectedDate) return;

    const loadSentenceData = async () => {
      setLoading(true);
      setError('');

      try {
        const month = selectedDate.substring(0, 6);
        const response = await fetch(`/data/sentences${month}.json`);

        if (!response.ok) {
          throw new Error('데이터를 불러올 수 없습니다.');
        }

        const data: SentenceData = await response.json();
        setSentenceData(data);
        setSentences(data.contents.sentences);
      } catch (err) {
        setError(err instanceof Error ? err.message : '알 수 없는 오류가 발생했습니다.');
        setSentences([]);
      } finally {
        setLoading(false);
      }
    };

    loadSentenceData();
  }, [selectedDate]);

  const handleMonthChange = (month: string) => {
    setSelectedMonth(month);
    const monthDates = availableFiles.filter(date => date.startsWith(month));
    if (monthDates.length > 0) {
      setSelectedDate(monthDates[0]);
    }
  };

  const handleDateChange = (date: string) => {
    setSelectedDate(date);
  };

  const formatDateDisplay = () => {
    if (!sentenceData) return '';
    const year = selectedDate.substring(0, 4);
    const month = selectedDate.substring(4, 6);
    const day = selectedDate.substring(6, 8);
    return `${year}년 ${month}월 ${day}일`;
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>중국어 문장 학습</h1>
        {sentenceData && (
          <div className="date-info">{formatDateDisplay()}</div>
        )}
      </header>

      {availableFiles.length > 0 && (
        <Navigation
          availableDates={availableFiles}
          selectedMonth={selectedMonth}
          selectedDate={selectedDate}
          onMonthChange={handleMonthChange}
          onDateChange={handleDateChange}
        />
      )}

      <main className="content">
        {loading && <div className="loading">데이터를 불러오는 중...</div>}

        {error && (
          <div className="error">
            <h2>오류 발생</h2>
            <p>{error}</p>
          </div>
        )}

        {!loading && !error && sentences.length > 0 && (
          <div className="sentences-container">
            {sentences.map((sentence) => (
              <SentenceCard key={sentence.id} sentence={sentence} />
            ))}
          </div>
        )}
      </main>
    </div>
  );
};

export default App;
